INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1, 'wc-admin-welcome-note', 'info', 'en_US', 'New feature(s)', 'Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-12 14:22:21', NULL, 0),
(2, 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Looking for the Store Notice setting?', 'It can now be found in the Customizer.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-12 14:22:21', NULL, 0),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-12 14:22:24', NULL, 0),
(4, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Welcome to WooCommerce! Set up your store and start selling', 'We\'re here to help you going through the most important steps to get your store up and running.', 'info', '{}', 'actioned', 'woocommerce-admin', '2020-05-12 08:22:34', NULL, 0),
(5, 'wc-admin-mobile-app', 'info', 'en_US', 'Встановіть мобільний додаток Woo', 'Встановіть мобільний додаток WooCommerce для керування замовленнями, отримання повідомлень про продажі та перегляду ключових показників - де б ви не знаходилися.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-05-14 17:40:17', NULL, 0),
(6, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Поради, оновлення товарів та нові ідеї', 'Ми тут для вас - отримуйте поради, оновлення продуктів та натхнення прямо на свою поштову скриньку.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-05-14 17:40:17', NULL, 0),
(7, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Review your experience', 'If you like WooCommerce Admin please leave us a 5 star rating. A huge thanks in advance!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-15 22:54:29', NULL, 0),
(8, 'wc-admin-facebook-extension', 'info', 'en_US', 'Маркетинг на Facebook', 'Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.', 'thumbs-up', '{}', 'unactioned', 'woocommerce-admin', '2020-05-15 22:54:29', NULL, 0),
(9, 'wc-admin-personalize-store', 'info', 'en_US', 'Персоналізуйте домашню сторінку вашого магазину', 'Домашня сторінка — це один із важливіших пунктів входу до вашого магазину. Якщо вона правильно оформлена, це може покращити конверсії та залучення користувачів. Не забудьте персоналізувати домашню сторінку, яку ми створили для вашого магазину під час запуску.', 'notice', '{}', 'unactioned', 'woocommerce-admin', '2020-05-18 21:06:13', NULL, 0),
(10, 'wc-admin-orders-milestone', 'info', 'en_US', 'Перше замовлення', 'Вітаємо з отриманням першого замовлення від покупця! Дізнайтеся, як керувати своїми замовленнями.', 'trophy', '{}', 'unactioned', 'woocommerce-admin', '2020-05-20 12:22:19', NULL, 0);
